
package forloop;

public class averagenumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int count = 0;
		  int sum = 0;
			for(int i = 23; i<= 34;i++) {
//				System.out.println(i);
				sum += i;
				count++;

			}

			System.out.println(sum/count);

		}
	

}
