package forloop;

public class vowelscountnymber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int count = 0;
			for(char i = 'D'; i<= 'Y';i++) {
				if(i == 'E' || i == 'I' ||i == 'O' || i== 'U') {
				    count++;

				}
			}

			System.out.println(count);

		}
	

}
