package whiledowhile;

public class factorialnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int number = 5;
		 int i=1;
	     int f = 1;
	     while(i <= number) {
              f*=i;
	    	 i++;
	     }  
		 System.out.println(f);
		}
	

}
